mapper.select:
SELECT custid, name, address, phone
  FROM customer
 WHERE custid = #{id}

mapper.update:
UPDATE customer
   SET name    = #{name},
       address = #{address},
       phone   = #{phone}
 WHERE custid  = #{custid}

mapper.select:
	SELECT custid, name, address, phone, email
  	FROM customer
 	WHERE email = #{email}
