mapper.select:
SELECT COUNT(*)
  FROM order_details od
  JOIN orders o ON od.orderid = o.orderid
 WHERE od.bookid = #{bookid}
   AND o.custid = #{custid}
   AND o.status = 'ORDERED'

mapper.update:
UPDATE review
   SET rating = #{rating},
       content = #{content},
       regdate = NOW()
 WHERE review_id = #{reviewId} 
   AND custid = #{custid}

mapper.delete:
DELETE FROM review
 WHERE review_id = #{reviewId} 
   AND custid = #{custid}
